import React, { useState } from "react";
import {
  StyleSheet,
  SafeAreaView,
  View,
  ScrollView,
  TextInput,
  Text,
  FlatList,
  TouchableOpacity,
  KeyboardAvoidingView,
  Alert,
  Platform,
  animationType,
} from "react-native";
import { Modal, Center, Input, Button, FormControl } from "native-base";
import Layout from "../components/Layout";
import Icon from "react-native-vector-icons/MaterialIcons";
import AsyncStorage from "@react-native-async-storage/async-storage";
const COLORS = { primary: "#1f145c", white: "#fff" };
import axios from "axios";

import { useEffect } from "react";

const Todos = () => {
  const [todos, setTodos] = useState([]);
  const [modalVisible, setModalVisible] = useState(true);
  const [showModal, setShowModal] = useState(false);

  const getTodos = async () => {
    /*  setIsLoading(true); */
    try {
      const res = await axios.get(
        "https://api.kontenbase.com/query/api/v1/b97b9784-95d8-4abd-806b-18875d222b49/todos"
      );

      setTodos(res.data);
      console.log(res);
    } catch (error) {
      alert("Data Not Found");
      /* setIsLoading(false); */
    }
  };

  const [title, setTitle] = useState();
  const addTodo = async () => {
    try {
      const data = {
        title,
      };

      const adddata =
        "https://api.kontenbase.com/query/api/v1/b97b9784-95d8-4abd-806b-18875d222b49/todos";

      const response = await axios.post(`${adddata}`, data);
      setTitle("");
      getTodos();
      console.log(response);
    } catch (error) {
      console.log(error);
    }
  };

  const [action, setAction] = useState(false);
  const handleDelete = async (id) => {
    try {
      console.log(id);
      await axios.delete(
        `https://api.kontenbase.com/query/api/v1/b97b9784-95d8-4abd-806b-18875d222b49/todos/${id}`
      );
      setAction(!action);
    } catch (error) {
      console.log(error);
    }
  };

  const [editTodo, setEditTodo] = useState(false);
  const handleEdit = async (id) => {
    try {
      console.log(id);
      await axios.patch(
        `https://api.kontenbase.com/query/api/v1/b97b9784-95d8-4abd-806b-18875d222b49/todos/${id}`
      );
      setEditTodo(!action);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getTodos();
  }, [action]);

  /*  const markTodoComplete = (todoId) => {
      const newTodosItem = todos.map((item) => {
        if (item.id == todoId) {
          return { ...item, completed: true };
        }
        return item;
      });
  
      setTodos(newTodosItem);
    }; */

  /*  const editTodo = () => {
      if (textInput == "") {
        Alert.alert("Error", "Please input todo");
      } else {
        const newTodo = {
          id: Math.random(),
          task: textInput,
          completed: true,
        };
        setTodos([...todos, newTodo]);
        setTextInput("");
      }
    }; */

  /*   const deleteTodo = (todoId) => {
      const newTodosItem = todos.filter((item) => item.id != todoId);
      setTodos(newTodosItem);
    };
  
    const clearAllTodos = () => {
      Alert.alert("Confirm", "Clear todos?", [
        {
          text: "Yes",
          onPress: () => setTodos([]),
        },
        {
          text: "No",
        },
    ]);
}; */

  return (
    <View style={{ height: "100%" }}>
      <Center>
        <Modal isOpen={showModal} onClose={() => setShowModal(false)}>
          <Modal.Content maxWidth="400px">
            <Modal.CloseButton />
            <Modal.Header>Edit Todo</Modal.Header>
            <Modal.Body>
              <FormControl>
                <FormControl.Label>Title</FormControl.Label>
                <Input />
              </FormControl>
            </Modal.Body>
            <Modal.Footer>
              <Button.Group space={2}>
                <Button
                  variant="ghost"
                  colorScheme="blueGray"
                  onPress={() => {
                    setShowModal(false);
                  }}
                >
                  Cancel
                </Button>
                <Button
                  onPress={() => {
                    setShowModal(false);
                  }}
                >
                  Save
                </Button>
              </Button.Group>
            </Modal.Footer>
          </Modal.Content>
        </Modal>
      </Center>

      <View>
        {todos.map((item) => {
          return (
            <View style={styles.listItem}>
              <View style={{ flex: 1 }}>
                <Text
                  style={{
                    fontWeight: "bold",
                    fontSize: 15,
                    color: COLORS.primary,
                    // textDecorationLine: todo?.completed ? "line-through" : "none",
                  }}
                >
                  {item.title}
                </Text>
              </View>
              <TouchableOpacity>
                <View style={[styles.actionIcon, { backgroundColor: "green" }]}>
                  <Icon name="done" size={20} color="white" />
                </View>
              </TouchableOpacity>
              <TouchableOpacity>
                <View style={styles.actionIcon}>
                  <Icon
                    onPress={() => handleDelete(item._id)}
                    name="delete"
                    size={20}
                    color="white"
                  />
                </View>
              </TouchableOpacity>
              <TouchableOpacity>
                <View style={styles.actionIcon}>
                  <Icon
                    onPress={() => setShowModal(true)}
                    name="edit"
                    size={20}
                    color="white"
                  />
                </View>
              </TouchableOpacity>
            </View>
          );
        })}
      </View>
      <View>
        <KeyboardAvoidingView
          behavior={Platform === "android" ? "padding" : "height"}
          style={styles.inputContainer}
        >
          <TextInput
            style={{
              padding: 15,
              marginHorizontal: -20,
              borderRadius: 50,
              border: "none",
            }}
            value={title}
            placeholder="Add Todo"
            onChangeText={(text) => setTitle(text)}
          />

          <TouchableOpacity onPress={addTodo}>
            <View style={styles.iconContainer}>
              <Icon name="add" color="white" size={30} />
            </View>
          </TouchableOpacity>
        </KeyboardAvoidingView>
      </View>
    </View>
  );
};

